#include <ThingSpeak.h>#include <Ethernet.h>#include <SPI.h>byte mac[] = { 0xAA, 0xAB, 0xBC, 0xEF, 0xFE, 0xED }; //assign your own MAC addressEthernetClient client;unsigned long channelNumber = 422685;const char * writeAPI = "6DD202QAT04Z3WPL";void setup() {	Serial.begin(9600);	pinMode(8, OUTPUT);	Ethernet.begin(mac);	ThingSpeak.begin(client);}void loop() {	float voltageSensor = analogRead(A3);	float currentIn = analogRead(A4);	float currentOut = analogRead(A5);	// Convert the analog reading (which goes from 0 - 1023) to a voltage (0 - 5V):	//2.87	float voltage = (voltageSensor * 2.87 * (5.0 / 1023.0)) + 1.3;	if (voltage >= 13.8) {		digitalWrite(8, LOW);	}	else {		digitalWrite(8, HIGH);	}	// print out the value you read:	Serial.print("Voltage = ");	Serial.print(voltage);	Serial.print("     currentIn = ");	Serial.print(currentIn);	Serial.print("     currentOut = ");	Serial.println(currentOut);	ThingSpeak.setField(1, voltage);	ThingSpeak.setField(2, currentIn);	ThingSpeak.setField(3, currentOut);	ThingSpeak.writeFields(channelNumber, writeAPI);	delay(20000); //wait 15 sec before updating}